{
	var i;
	
	possible = 1;

	numberIncorrectGuesses = 0;

	lettersFound = 0;

	for (i=1; i <27; i++)
	{
		// add 64 to get correct ASCII value
		document.getElementById("a"+i).innerHTML=String.fromCharCode(i+64);
		letterArray[i] = 0;
	}

	document.getElementById('hangmanimage').innerHTML = "<img src='0.gif' />";
	
}

